import numpy as np
class TesseractPIM:
 def __init__(self,cores=512,internal_bw=8000,off_chip_bw=640):
  self.cores=cores;self.internal_bw=internal_bw;self.off_chip_bw=off_chip_bw;self.states=['idle','prefetch','ready','process']
 def conditional_rotation(self,message_arrived=True,threshold_met=True):
  state='idle';if message_arrived:state='prefetch';if threshold_met:state='ready' if state=='prefetch' else 'process';return state
 def energy_reduction(self,baseline_energy=100,speedup=10,power_factor=1.4):
  tesseract_energy=power_factor*(baseline_energy/speedup);reduction=(1-(tesseract_energy/baseline_energy))*100;return reduction
pim=TesseractPIM();state=pim.conditional_rotation();reduction=pim.energy_reduction();print(f'State: {state}\nReduction: {reduction}%')
